mapper.update:

UPDATE 	TMSTEVENT
SET 			EVENT_NAME = #{param2}
				,EVENT_STARTDATE = #{param3}
				,EVENT_ENDDATE = #{param4}
				,FILENAME = #{param5}
				,EVENT_FLAG = #{param6}
WHERE 		EVENT_NO = #{param1}

mapper.select:

SELECT 	FILENAME
FROM 	TMSTEVENT
WHERE 	EVENT_NO = #{param1}

mapper.update:

UPDATE 	TMSTEVENT
SET 			EVENT_MEMBER_FLAG = 'Y'
				,M_NO1 = #{param2}
				,M_NO2 = #{param3}
				,M_NO3 = #{param4}
WHERE 		EVENT_NO = #{param1}

mapper.select:

SELECT MAX(DELIVERY_NO)
FROM THISDELIVERY
WHERE M_NO = #{param1}
AND EVENT_NO = #{param2}

mapper.insert:

INSERT INTO THISPUSHAL
(
	AL_NO, M_NO, AL_CONTENT, AL_GUBUN, ORDER_NO, DELIVERY_NO, AL_TIME
)
SELECT  		(SELECT NVL(MAX(AL_NO),0) + 1 FROM THISPUSHAL)
                ,#{param1}
                ,#{param3}
                ,3                   	
                ,NULL
                ,#{param2}
                ,SYSDATE
FROM    	THISDELIVERY A
WHERE   	A.M_NO 		        = #{param1}
AND      	A.DELIVERY_NO   	= #{param2}
