INSERT INTO THISPUSHAL
(
	AL_NO, M_NO, AL_CONTENT, AL_GUBUN, ORDER_NO, DELIVERY_NO, AL_TIME
)
SELECT  		(SELECT NVL(MAX(AL_NO),0) + 1 FROM THISPUSHAL)
            	,#{param1}
            	,#{param3}
            	,2                   	
            	,#{param2}
            	,NULL
            	,SYSDATE
FROM    	THISORDER A
WHERE   	A.M_NO 		= #{param1}
AND      	A.ORDER_NO 	= #{param2}
