mapper.select:

SELECT DISTINCT  
            A.ORDER_NO, B.S_NO
FROM    THISORDER A LEFT JOIN THISORDERDTL B ON A.ORDER_NO = B.ORDER_NO
WHERE   A.M_NO = #{param1}
AND       A.ORDER_NO = #{param2}


SELECT 	NVL(SUM(NVL(POINT_OUT,0)),0) 
FROM 	THISPOINT 
WHERE 	M_NO = #{param1}
AND 		ORDER_NO = #{param2}

mapper.update:

UPDATE  TMSTMEMBER
SET        M_POINT = M_POINT + #{param2}
WHERE   M_NO = #{param1};

mapper.select:

				SELECT  (
				                CASE WHEN B.COUPON_NO IS NULL THEN 0
				                        ELSE    B.COUPON_NO END
				            ) AS COUPON_NO            
				FROM    THISORDER A LEFT JOIN THISUSECOUPON B 	ON 	A.COUPON_NO 	= B.COUPON_NO
                                                											AND 	A.M_NO 			= B.M_NO;
                WHERE 	A.M_ID = #{param1}
                AND 		A.ORDER_NO = #{param1}

mapper.update:

UPDATE 	THISCOUPON
SET 			COUPON_QTY = COUPON_QTY + 1
WHERE 		M_NO = #{param1}
AND 			COUPON_NO = #{param2}


UPDATE 	THISUSECOUPON
SET 			COUPON_USEFLAG = 'N'
WHERE 		M_NO = #{param1}
AND 			COUPON_NO = #{param2}
